import tableData from '../../Data/tableData.json';
import {Container} from 'semantic-ui-react';

import { Icon, Label, Menu, Table, Modal, Header, Button, Checkbox, Dropdown, Grid, Segment  } from 'semantic-ui-react';
import _ from 'lodash';




export default function Job() {
  
        var tableName;
    var flag=0, value='All', value1, display=0;
      var completed= true;
    const [state, dispatch] = React.useReducer(exampleReducer, {
    column: null,
    data: tableData,
    direction: null,
  })
  const { column, data, direction } = state
  const [open, setOpen] = useState(false)
  const [isChecked, setChecked] = useState(true);
    const [selectValue, setselectValue] = useState('');
    const [selectValue1, setselectValue1] = useState('');
     const [searchValue, setsearchValue] = useState('');

  
       const Category = ["None", "Equipment", "Paddock", "Fuel Tank", "Silo", "Livestock"];
     const Priority = ["Low", "Normal", "High", "Safety"];
     const Status = ["Raised", "Raised and Assigned", "In Progress", "On Hold", "Completed"];
     let type = null;
  
 
  let options = null;
  

  if (selectValue === "Priority") 
    type = Priority;
 if (selectValue === "Category") 
    type = Category;
     if (selectValue === "Status") 
    type = Status;

  
 

   function ErrorMessage(props) {
  const Display = props.Display;
      
       if(!Display)
       { 
        return (
           <>
         <p style={{"text-align": "center", "color": "grey", "padding": "20px", "align-content": "bottom"}}> No record found</p> 
          
           </>
           );
       }
       
       else{
           return(
           <> </>
           );
       }
   }
   
 if(selectValue){
      
      flag=1;
     value=selectValue;
     if(selectValue=="Status")
         value1="Raised";
     if(selectValue=="Priority")
         value1="Low";
     if(selectValue=="Category")
         value1="None";
 }
    if(selectValue1){
    value1=selectValue1; }
    
       
   function HandleDropdown(props) {
  const Value = props.Value;
        if (type) {
    options = type.map((el) => <option value={el} key={el}>{el}</option>
                      );
  }
    switch (Value) {
    case 'Priority':
    case 'Status':
    case 'Category':
            return (<>
                          
 <div class="inputfield">
         <div class="custom_select"> 
       <select value={selectValue1} onChange={(e) => setselectValue1(e.target.value)}>              
   {options}
       </select>
       </div>
    </div>
    </>
)
     case 'Asset':
            return (
                <>
                <div class="inputfield">
        
                  <input onChange={(e)=>setsearchValue(e.target.value)} value={searchValue} class='input_value' type="text" placeholder="Search..." />
                  </div>
    </>
    )
    case 'Description':
           return  (<>
                          
 <div class="inputfield">
         
                <input onChange={(e)=>setsearchValue(e.target.value)} value={searchValue} class='input_value' type="text" placeholder="Search..." />
                 </div>
  
    </>
    )
               default:
                 return (<>
                         
  </>
)
        
}
}

function exampleReducer(state, action) {
  switch (action.type) {
    case 'CHANGE_SORT':
      if (state.column === action.column) {
        return {
          ...state,
          data: state.direction === 'ascending' ? _.orderBy(state.data, [action.column], ['desc']):(state.direction === 'null'?_.orderBy(state.data, [action.column], ['asc']) : tableData ),
          direction: state.direction === 'ascending' ? 'descending' : (state.direction === 'null'? 'ascending': 'null'),
        }
      }

      return {
        column: action.column,
        data:_.orderBy(state.data, [action.column], ['asc']), 
        direction: 'ascending',
      }
      case 'PRIORITY_SORT':
          const priority = {
              Low: 1,
              Normal: 2,
              High: 3,
              Safety: 4,
 
          };
                
      
             
         if (state.column === action.column) {
        return {
            
          ...state,
          data: state.direction === 'ascending' ?  _.sort((a, b) => priority[b] - priority[a]):(state.direction === 'null'?_.sort((a, b) => priority[a] - priority[b]) : tableData ),
          direction: state.direction === 'ascending' ? 'descending' : (state.direction === 'null'? 'ascending': 'null'),
        }
      } 
    default:
      throw new Error()
  }
}

const items = tableData.filter((data)=>{

if(!isChecked && data.status == "Completed")
    return 
   if(flag)  
   { if(selectValue=="Status" && data.status == value1)   
          { display=1
              return data}
     else if(selectValue=="Priority" && data.priority == value1)   
           { display=1
              return data}
     else if(selectValue=="Category" && data.category == value1)   
          { display=1
              return data}
    else if(selectValue=="All" || selectValue=="Description" || selectValue=="Asset")   
          { 
              if(searchValue==""){
                   display=1
              return data
              }
             else if(data.description.toLowerCase().includes(searchValue.toLowerCase()) || data.asset.toLowerCase().includes(searchValue.toLowerCase())){
                 
           display=1
          return data
      }
             
          
          }
   
   
    else 
        return
   }
   
  
   else {
       display=1
       return data
   }
       
      
    }).map(({status, priority, category, asset, description,duedate,lastupdate }) => (
         
          <Table.Row key={description}>
       
            <Table.Cell> <IconDisplay dataValue={status} /></Table.Cell>
            <Table.Cell> <IconDisplay dataValue={priority} /></Table.Cell>
            <Table.Cell>{description}</Table.Cell>
            <Table.Cell>{category}</Table.Cell>
            <Table.Cell>{asset}</Table.Cell>
            <Table.Cell>{duedate}</Table.Cell>
            <Table.Cell>{lastupdate}</Table.Cell>
            <Table.Cell> <Editjob/></Table.Cell>
       
          </Table.Row>      
   ))
   
   
  return(

   <div>
      <div className="v-application">
          
          <HeaderSection/>
    </div>
      <Container>
   <Subheader>
    <h3>Jobs </h3><span>&nbsp;&nbsp;</span>
   <Modal style={{ "height": "35%", "width": "30%","position": "relative", "background-color": "rgb(0 0 0 / 62%)" }}
      centered={true}
      open={open}
      trigger={<GearIcon style={{"cursor":"pointer"}}/>
      }
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
    >
      <Header icon='setting' content='Settings' />
      <Modal.Content>
       <Checkbox toggle onClick={() => setChecked(!isChecked)} 
        checked={isChecked}/>
            
<span style={{"color":"grey","font-size":"20px"}}>  Show Completed </span>
      </Modal.Content>
      <Modal.Actions>
        <Button color='red' onClick={() => setOpen(false)}>
          <Icon name='remove'  /> Close
        </Button>
        
      </Modal.Actions>
    </Modal>   
      

      <div className="spacer"></div>
   
        <Grid.Column >
             
        <div class="inputfield">
        <div class="custom_select">  
       <select onChange={(e) => {setselectValue(e.target.value);
                         
                       setselectValue1(e.target.value=="Status"?"Raised":
                         (e.target.value=="Priority"?"Low":
                         (e.target.value=="Category"?"None":" ")));
                         
                       }}>
         
          <option value="All">All</option>
          <option value="Status">Status</option>
          <option value="Priority">Priority</option>
          <option value="Description">Description</option>
          <option value="Asset">Asset</option>
          <option value="Category">Category</option>
      </select>
</div>
</div>
        </Grid.Column>
        <Grid.Column style={{"margin-left": "10px", "margin-right": "20px"}}>
               
       <HandleDropdown Value={selectValue}/>
        </Grid.Column>
 
   <Addjob/>
 
     
     
</Subheader>
    <Table sortable singleLine selectable color="green" key="green" >
      <Table.Header>
        <Table.Row>
              <Table.HeaderCell
            sorted={column === 'status' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'status' })}
            >
            Status
          </Table.HeaderCell>
          <Table.HeaderCell
             sorted={column === 'priority' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'priority' })}
            >
            Priority
          </Table.HeaderCell>
          <Table.HeaderCell
            sorted={column === 'description' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'description' })}
          >
            Description
          </Table.HeaderCell>
          <Table.HeaderCell
            sorted={column === 'category' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'category' })}
          >
            Category
          </Table.HeaderCell>
          <Table.HeaderCell
            sorted={column === 'asset' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'asset' })}
          >
            Asset
          </Table.HeaderCell>
         <Table.HeaderCell
            sorted={column === 'duedate' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'duedate' })}
        >
            Due date
          </Table.HeaderCell> 
        <Table.HeaderCell
            sorted={column === 'lastupdate' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'lastupdate' })}
        >
            Last Update
          </Table.HeaderCell>
          
        <Table.HeaderCell>
            Actions
          </Table.HeaderCell>
        </Table.Row>
      </Table.Header>

      <Table.Body>
        {items}
 
      </Table.Body>
 <Table.HeaderCell colSpan='8'>
     
     <ErrorMessage Display={display}/>
        
     </Table.HeaderCell>
    <Table.Footer>
      <Table.Row>
        <Table.HeaderCell colSpan='8'>
          <Menu floated='right' pagination>
            <Menu.Item as='a' icon>
              <Icon name='chevron left' />
            </Menu.Item>
            <Menu.Item as='a'>1</Menu.Item>
            <Menu.Item as='a'>2</Menu.Item>
            <Menu.Item as='a'>3</Menu.Item>
            <Menu.Item as='a'>4</Menu.Item>
            <Menu.Item as='a' icon>
              <Icon name='chevron right' />
            </Menu.Item>
          </Menu>
        </Table.HeaderCell>
      </Table.Row>
    </Table.Footer>
      
  </Table>
  
     
 </Container> 
<Footer/>
</div>

      );
}
