function exampleReducer(state, action) {
  switch (action.type) {
    case 'CHANGE_SORT':
      if (state.column === action.column) {
        return {
          ...state,
          data: state.direction === 'ascending' ? _.orderBy(state.data, [action.column], ['desc']):(state.direction === 'null'?_.orderBy(state.data, [action.column], ['asc']) : tableData ),
          direction: state.direction === 'ascending' ? 'descending' : (state.direction === 'null'? 'ascending': 'null'),
        }
      }

      return {
        column: action.column,
        data:_.orderBy(state.data, [action.column], ['asc']), 
        direction: 'ascending',
      }
      case 'PRIORITY_SORT':
          const priority = {
              Low: 1,
              Normal: 2,
              High: 3,
              Safety: 4,
 
          };
             const priorityData = ["Low", "Normal", "High", "Safety"];
           priorityData
        .sort((a, b) => priority[a] - priority[b])
        .map(c => <div className={c} key={c}>{c}</div>)
         if (state.column === action.column) {
        return {
          ...state,
          data: state.direction === 'ascending' ? _.orderBy(state.data, [action.column], ['desc']):(state.direction === 'null'?_.orderBy(state.data, [action.column], ['asc']) : tableData ),
          direction: state.direction === 'ascending' ? 'descending' : (state.direction === 'null'? 'ascending': 'null'),
        }
      } 
    default:
      throw new Error()
  }
}

function IconDisplay(props) {
  const dataValue = props.dataValue;
    switch (dataValue) {
    case 'Raised':
            return <Icon name='blue circle outline' size='large'/>
    case 'Completed':
            return <Icon name='green circle' size='large'/>
    case 'Raised and Assigned':
            return <Icon name='blue circle'size='large'/>
    case 'On Hold':
           return <Icon name='red circle'size='large'/>
    case 'In Progress':
           return <Icon name='orange circle'size='large'/>
    case 'Low':
           return <Icon name='angle down'size='large'/>
    case 'High':
            return <Icon name='red angle double up'size='large'/>
    case 'Normal':
            return <Icon name='blue angle up'size='large'/>
    case 'Safety':
           return <Icon name='orange exclamation triangle'size='large'/>
               
             
        
}
}


