import React, { useState } from 'react';

import FarmTable from './farmTable';
import Farmpopup from './FarmListPopup/farmpopup';
import '../../Pages.css';
import farm from "../../../../Data/farmData";
import { Grid  } from 'semantic-ui-react';

export default function Farms() {

  
  const [farmid, setfarmid] = useState(farm.farm_id);
  let options= null;
  function FarmDropdown() {
   
          
              options = farm.map((el) => <option value={el.farm_id} key={el.farm_id}>{el.value}</option>
                        );
          
     if(farmid=== null)
     {
      return (<>
                            
        <div class="inputfield">
            <div class="custom_select"> 
            <select >            
            <option>No data available</option>
                </select>
            </div>
        </div>
        </>
        )
     }
     else{
              return (<>
                            
                  <div class="inputfield">
                      <div class="custom_select"> 
                      <select key={farmid} value={selectFarm} onChange={(e) => {setfarmid(e.target.value);}}>            
                          {options}
                          </select>
                      </div>
                  </div>
                  </>
                  )
          }
  
  
          
  }
  
  
 
   
  return(

<div  className= "table-size">
    
    <div className= "subheader">  
   
        <h1 style={{"color": "black"}}>Farms </h1><span>&nbsp;&nbsp;</span>
        
      
   {/*--------------------------------------------Add edit delete farm popup----------------------*/}
       <div className="icon-align">
           
          <Farmpopup farmid={farmid}/>
      </div>  
      <Grid.Column style={{"margin-left": "10px"}}>
         <FarmDropdown/>
      </Grid.Column>
      <div className="spacer"></div>
    
    </div>
     {/*------------------------------------Farm Tables Paddock, silos, fuel tanks----------------------*/}
    <FarmTable farm={farmid}/>
</div>

      );
}
