import * as React from 'react';
import Box from '@mui/material/Box';
import { Useform, Form } from '../Useform';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import FormControl from '@mui/material/FormControl';
import { InputLabel, MenuItem, Grid, Select as MuiSelect } from '@material-ui/core';
import Paddock1 from '../services/paddock.png'
import SelectCheck from './SelectCheck';

export default function Farms(props) {
    const [open, setOpen] = React.useState(false);
    const [farm, setfarm] = React.useState("");
    const [farm1, setfarm1] = React.useState("");
    const [farm2, setfarm2] = React.useState("");
    
    const [paddock2, setpaddock2] = React.useState("");  
    const [paddock, setpaddock] = React.useState("");  
    const [paddock1, setpaddock1] = React.useState("");   
    const [img, setimg] = React.useState('');
    const [openfarm, setopenfarm] = React.useState(false);
    const [bool, setbool] = React.useState(false);

    const farm_options =[
        {id:'1', title:'Farm1'},
        {id:'2', title:'Farm2'},
     
    ]
    const paddock_options =[
        {id:'1', title:'Paddock1'},
        {id:'2', title:'Paddock2'},
     
    ]
    
const initialValues = {
  
    farms: '',
    paddocks: '',
    
}
const {
    values,
    setValues,
    handleInputChange
} = Useform(initialValues);

    const handleChange = (event, value) => {
       
        setfarm(value.props.children);
        setfarm1(event.target.value);
    };
    const handleClose = () => {
       
        setopenfarm(false);
      
    };
    const handleClose1 = () => {
       
        setOpen(false);
      
    };
   

    const handleClickfarm = (event) => {
        setopenfarm(true)
    }

    const handleOk = (event) => {
        setfarm2(farm);
        setpaddock2(paddock);
            setopenfarm(false); 
            setimg(Paddock1);
            setbool(true);
        
    }
    const handleChange1 = (event, value) => {
        setpaddock(value.props.children);
        setpaddock1(event.target.value);
    }
    const handleClickOpen = () => {
       
            setOpen(true);
     
    };
    const handleOk1 = (event) => {
        setfarm2(farm);
        setpaddock2(values.paddocks);
            setOpen(false); 
            setimg(Paddock1);
          
        
    }
   const { name, label, restart } = props;
    
    return (
        <div>
           
            <Button style={{display: bool ? 'block' : 'none' }} color="inherit" onClick={handleClickOpen} size="large" fullWidth="true">
                <Grid container spacing={0}>
                    <Grid item xs={0} sm={0} md={0}>
                        <img src={img} className="img" alt="tractor"></img>
                    </Grid>
                    <Grid item xs={3} md={0}>
                        <p>{farm2}</p>
                        <InputLabel>{paddock2}</InputLabel>
                     </Grid>
                </Grid>
            </Button>
           
                            <Grid item xs={0} sm={0}>
                                <div><p>Estimated Total: 0 ha</p></div>
                            </Grid>
                            <Grid item xs={0} sm={0}>
                                <span align="right"><Button variant="text" color="primary" onClick={handleClickfarm}><b>ADD</b></Button></span>
                               
                                <Dialog open={openfarm} onClose={handleClose}>
                                <DialogTitle>Farms/Paddocks</DialogTitle>
                                <DialogContent>
                                <Box component="form" sx={{ display: 'grid' }}>
                        <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                            <InputLabel>{label}</InputLabel>
                            
                            <MuiSelect
                                label="Farm"
                                name="farm"
                                value={values.farms}
                                onChange={handleInputChange}
                                displayEmpty>
                                
                                {
                                    farm_options.map(
                                        item => (<MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>)
                                    )
                                }
                            </MuiSelect>
                        </FormControl>
                        <SelectCheck
                        name="paddock"
                        label="Paddocks"
                        value={values.farms}
                        onChange={handleInputChange}
                        restart={bool}
                        choice="paddock"

                    />
                    </Box>
                                </DialogContent>
                                <DialogActions>
                                    <Button onClick={handleClose} color="primary">CANCEL</Button>
                                    <Button onClick={handleOk} color="primary">SAVE</Button>
                                </DialogActions>
                            </Dialog>
                            </Grid>
                            <Dialog open={open} onClose={handleClose}>
                                <DialogTitle>Farms/Paddocks</DialogTitle>
                                <DialogContent>
                                <Box component="form" sx={{ display: 'grid' }}>
                                <SelectCheck
                        name="paddock"
                        label="Paddocks"
                        value={paddock1}
                        onChange={handleChange}
                        restart={bool}
                        choice="paddock"

                    />
                        <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                            <InputLabel>Paddock</InputLabel>
                            <MuiSelect
                                label="Paddock"
                                name="paddock"
                                value={paddock1}
                                onChange={handleChange1}
                                displayEmpty>
                                
                                {
                                    paddock_options.map(
                                        item => (<MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>)
                                    )
                                }
                            </MuiSelect>
                        </FormControl>
                    </Box>
                                </DialogContent>
                                <DialogActions>
                                    <Button onClick={handleClose1} color="primary">CANCEL</Button>
                                    <Button onClick={handleOk1} color="primary">SAVE</Button>
                                </DialogActions>
                            </Dialog>
            
        </div>
    );
}
