import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import { Useform, Form } from '../Useform';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import FormControl from '@mui/material/FormControl';
import SelectCheck from './SelectCheck';
import { InputLabel, MenuItem, Grid, Select as MuiSelect } from '@material-ui/core';
import TextField from '@mui/material/TextField';
import tractor from '../services/tractor.png'

export default function Equipment(props) {
    const [open, setOpen] = React.useState(false);
    const [equipment, setequipment] = React.useState("");
    const [cat, setcat] = React.useState({});
    const [img, setimg] = React.useState(tractor);
    const [img1, setimg1] = React.useState(tractor);
    var changeequipment = 0;
    var flag=0;
    const arr = {};
    const [field, setfield] = React.useState("None")
    const [fieldfarm, setfieldfarm] = React.useState("")
    const [subfield, setSubfield] = React.useState('NONE');
    const [rate, setRate] = React.useState('60');
    const [rateunit, setRateunit] = React.useState('l/Ha');
    const [fieldval, setfieldval] = React.useState("equipment")
    const [subfieldval, setSubfieldval] = React.useState('NONE');
    const [c, setc] = React.useState({});

    const initialValues = {
  
        nozzles: '',
        equipment: '',
        dueDate: new Date(),
        priority: '',
        started: '',
        completed: '',
        restartjob: '',
        description: "",
        notes: "",
    }
    
    const {
        values,
        setValues,
        handleInputChange
    } = Useform(initialValues);


    const handleChange = (event, value) => {
        setequipment(event.target.value);
        changeequipment = event.target.value;
        setfieldval(value.props.children)
        setcat({})
        flag=0;
        
        if (changeequipment != "") {
            setc(arr[0].data);
            setSubfieldval("");
            setfieldfarm("");
          
        }
        else{
            setc({});
            setfieldval("equipment")
            setSubfieldval("None");
            setfieldfarm("");
        
        }
    };

  
    const handleClickOpen = () => {
      
            setOpen(true);
     
    };

    const handleClose = (event, reason) => {
        if (reason !== 'backdropClick') {
            setOpen(false);
        }
    };

    const { name, label, value, onChange, options, restart } = props;
    return (
        <div>
            <Button color="inherit" onClick={handleClickOpen} size="large" fullWidth="true">
                <Grid container spacing={0}>
                    <Grid item xs={0} sm={0} md={0}>
                        <img src={img} className="img" alt="tractor"></img>
                    </Grid>
                    <Grid item xs={6} md={3}>
                        <p>{field}</p>
                        <InputLabel>Nozzles:{subfield}</InputLabel>
                        <p >{rate} {rateunit}</p>

                    </Grid>
                </Grid>
            </Button>
            <Dialog open={open} onClose={handleClose}>
            <DialogTitle>Spray Equipment</DialogTitle>
            <DialogContent>
                <Box component="form" sx={{ display: 'grid' }}>
                    <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                        <TextField
                            variant="standard"
                            name="description"
                            label="Description"
                            value=""
                            
                            required
                            autocomplete="off"
                        />
                        <SelectCheck
                        name="nozzle"
                        label="Nozzles"
                        value={values.nozzles}
                        onChange={handleInputChange}
                        choice="nozzle"

                    />
                    <br></br>
                    <TextField
                            variant="standard"
                            name="description"
                            label="Operating pressure"
                            
                            required
                            autocomplete="off"
                        />
                        <br></br>
                            <InputLabel>Droplet size</InputLabel>
                            <MuiSelect
                                label="Droplet size"
                                name={name}
                                value={equipment}
                                onChange={handleChange}
                                displayEmpty>
                                <MenuItem value="">
                                    <em>None</em>
                                </MenuItem>
                                {
                                    options.map(
                                        item => (<MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>)
                                    )
                                }
                            </MuiSelect>
                    </FormControl>
                </Box>
            </DialogContent>
            <DialogActions>
                <Button onClick={handleClose} color="primary">CANCEL</Button>
                <Button onClick={handleClickOpen} color="primary">SAVE</Button>
            </DialogActions>
          </Dialog>
            
        </div>
    );
}
