import React, { useState } from "react";
import MaterialTable from "material-table";
import { Checkbox } from "semantic-ui-react";
import { Button, Dialog, DialogActions, DialogContent, TextField } from '@material-ui/core'
import { MTableCell } from "material-table";
import DialogTitle from '@mui/material/DialogTitle';
import Box from '@mui/material/Box';
import FormControl from '@mui/material/FormControl';
import '../../Pages.css';
import Tooltip from '@material-ui/core/Tooltip';
import CreateIcon from '@mui/icons-material/Create';
import AddCircleRoundedIcon from '@mui/icons-material/AddCircleRounded';
import {useTranslation,Trans} from 'react-i18next';
import Addspraylog from './NewSpraylog/Spraylognew';
import SampleContextProvider from './NewSpraylog/context/SampleContext';
import {Icon} from 'semantic-ui-react';
import {
    spraylogsData_withoutcomplete,
    spraylogsData_withcomplete,
} from "../../../../Data/SpraylogsData";

import { InputLabel, MenuItem, Grid, Select as MuiSelect } from '@material-ui/core';

export default function Chemicals() {

  const [chemical, setchemical] = React.useState("");
  const [chemical1, setchemical1] = React.useState("");
  const [chemical2, setchemical2] = React.useState("");
  
  const [paddock2, setpaddock2] = React.useState("");  
  const [paddock, setpaddock] = React.useState("");  
  const [unit, setunit] = React.useState("");   
  const [rate, setRate] = React.useState();
 
  const chemical_options =[
      {id:'1', title:'Chem1'},
      {id:'2', title:'Chem2'},
   
  ]
  const units = [
      { id: 1, title: 'ml/ha' },
      { id: 2, title: 'Kg/ha' },
      { id: 3, title: 'L/ha' },
      { id: 4, title: 'lb/ha' },
      { id: 5, title: 'oz/ha' },
      { id: 6, title: 'g/ha' },
      { id: 7, title: 'floz/ac' },
      { id: 8, title: 'qt/ac' },
      { id: 9, title: 'pt/ac' },
      { id: 10, title: 'oz/ac' },
      { id: 11, title: 'lb/ac' },
      { id: 12, title: 'g/ac' },
      { id: 13, title: 'Kg/1000L' },
      { id: 14, title: 't/ha' },
      { id: 15, title: 'L/Kg' },
      { id: 16, title: 'L/t' }
  ];
     
        
     



  const {t} =useTranslation();
  const [selected, toggleselected] = useState(false);
  const [description, setdescription] = React.useState("");
  const [bool, setbool] = React.useState(false);
  const [helper, sethelper] = React.useState("");
  const [dataedit, setdataedit] = React.useState("");
  


  const [open, setOpen] = React.useState(false);
    
    const handleClose = (event, reason) => {
        setOpen(false);

    };

    const [openAddSpraylog, setOpenAddSpraylog] = React.useState(false);
    const handleClickOpen = () => {
      setOpenAddSpraylog(true);
  };
    const handleCloseSpraylog = (event, reason) => {
      setOpenAddSpraylog(false);

    };

  const handleChange = (event) => {
    setdescription(event.target.value);
    setdataedit(event.target.value)
    if (event.target.value=='') {
        setbool(true)
        sethelper("Required")
    }
    else{
        setbool(false)
        sethelper("")
    }
};
const handleChange3 = (event) => {
  setRate(event.target.value);
  if (event.target.value=='') {
      setbool(true)
      sethelper("Required")
  }
  else{
      setbool(false)
      sethelper("")
  }
};


const handleOk = (event) => {
  setchemical2(chemical);
  setpaddock2(paddock);
   
  
}
const handleChange1 = (event) => {

  setunit(event.target.value);
}

  
  return (
   <div> 
     
         <Dialog open="true" onClose={handleClose}>
                                 <DialogTitle>Chemicals/Additives</DialogTitle>
                                     <DialogContent>
                                       <Box component="form" sx={{ display: 'grid' }}>
                                        <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                                         <InputLabel>Chemical</InputLabel>
                                            <MuiSelect
                                                label="Chemical"
                                                name="chemical"
                                                value={chemical1}
                                                onChange={handleChange}
                                                displayEmpty>
                                                
                                                {
                                                    chemical_options.map(
                                                        item => (<MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>)
                                                    )
                                                }
                                            </MuiSelect>
                                        </FormControl>
                                        <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                                        <Grid container alignItems="flex-end">
                                            <Grid >
                                                <TextField
                                                    variant="standard"
                                                    name="rate"
                                                    label="Application rate"
                                                    value={rate}
                                                    onChange={handleChange3}
                                                    error={bool}
                                                    helperText={helper}
                                                    autocomplete="off"
                                                    maxLength={120}
                                                  
                                                />
                                            </Grid>
                                             <Grid >
                                                <InputLabel>Unit</InputLabel>
                                                    <MuiSelect
                                                        maxLength={120}
                                                        label="Unit"
                                                        name="unit"
                                                        value={unit}
                                                        onChange={handleChange1}
                                                        displayEmpty>
                                                        
                                                        {
                                                            units.map(
                                                                item => (<MenuItem key={item.id} value={item.title}>{item.title}</MenuItem>)
                                                            )
                                                        }
                                                    </MuiSelect>
                                            </Grid>
                                        </Grid>
                                           
                                        </FormControl>
                                      </Box>
                                    </DialogContent>
                                    <DialogActions>
                                        <Button onClick={handleClose} color="primary">CANCEL</Button>
                                        <Button onClick={handleOk} color="primary">SAVE</Button>
                                    </DialogActions>
                                </Dialog>
          
          <Dialog open={openAddSpraylog} onClose={handleCloseSpraylog} maxWidth="100" scroll="paper">
                <DialogTitle sx={{ fontSize: 24, fontWeight: 'large' }}>{description}</DialogTitle>
                <DialogContent dividers>
                <SampleContextProvider>
                    <Addspraylog />
                    </SampleContextProvider>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseSpraylog} color="primary">CANCEL</Button>
                    <Button onClick={handleCloseSpraylog} color="primary">SAVE</Button>
                </DialogActions>
            </Dialog>
   </div>        
                            
  )     
}
