import React, { useState } from "react";
import { Button, Dialog, DialogActions, DialogContent, TextField } from '@material-ui/core'
import DialogTitle from '@mui/material/DialogTitle';
import Box from '@mui/material/Box';
import FormControl from '@mui/material/FormControl';
import { InputLabel, MenuItem, Grid, Select as MuiSelect } from '@material-ui/core';
import Paddock1 from '../services/paddock.png'
import PaddockData from '../../../../../../Data/PaddockData';
import farmData from '../../../../../../Data/farmData';

export default function Farms(props) {

    const [bool, setbool] = React.useState(false);
    const [helper, sethelper] = React.useState("");
    const [open, setOpen] = React.useState(false);
    const [openedit, setopenedit] = React.useState(false);

    const SpraylogFarmData = [
        {farms: 'Farm1',paddocks: 'Linta Paddock'},
        {farms: 'Farm3',paddocks: 'K2K Paddock'}
    ]

    const [allValues, setAllValues]= React.useState(
        {
          chemname: '',
          actives: '', 
          group: '', 
          runit: '', 
          rate: '',
          batch: ''
        }
      );
      const [EditValues, setEditValues]= React.useState(
        {
          chemname: '',
          actives: '', 
          group: '', 
          runit: '', 
          rate: '',
          batch: ''
        }
      );

      const handleClose= () => {
          setOpen(false);
          setopenedit(false)
      };
      const handleRemove= () => {
        setopenedit(false);

    };
      const handleChange = (e, value) => {     
        setAllValues({...allValues, [e.target.name]: e.target.value})  
    };


    const handleClickfarm = (event) => {
      setOpen(true)
    }

    const handleOk = (event) => {
      setopenedit(false)
      setOpen(false);
    };

    const handleChange1 = (e, value) => {

        setEditValues({...EditValues, [e.target.name]: e.target.value})  

    };

    const handleEdit = (e,d)=>{
      setEditValues(d);
      setopenedit(true);
     };




    return (
        <div>

             {SpraylogFarmData.map((data) => (         
                <Button onClick={(e)=>handleEdit(e,data)} color="inherit" size="large" fullWidth="true">
                 <Grid container spacing={0}>
                     <Grid item xs={0} sm={0} md={0}>
                         <img src={Paddock1} className="img" alt="tractor"></img>
                     </Grid>
                     <Grid item xs={3} md={0}style={{"textTransform": "none"}}>
                        <p style={{"margin-bottom": "0px"}}>{data.farms}</p>
                        <InputLabel>{data.paddocks}</InputLabel>
                     </Grid>
                 </Grid>
             </Button>             
             ))}
     <span align="right"><Button variant="text" color="primary" onClick={handleClickfarm}><b>ADD</b></Button></span>
         <Dialog open={open} onClose={handleClose}>
         <DialogTitle>Farms/Paddocks</DialogTitle>
                <DialogContent>
                  <Box component="form" sx={{ display: 'grid' }}>
                  <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                    <InputLabel>Farm</InputLabel>
                      <MuiSelect
                          label="Farm"
                          name="farms"
                          value={allValues.farms}
                          displayEmpty>
                          {
                            farmData.map(
                              (item, index) => (<MenuItem key={item.id} value={item.value} onClick={(event) => handleChange(event, item.value)}>{item.value}</MenuItem>)
                            )
                        }
                      </MuiSelect>
                  </FormControl>
                  <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                    <InputLabel>Paddock</InputLabel>
                    <MuiSelect
                        label="Paddock"
                        name="paddocks"
                        value={allValues.paddocks}
                          displayEmpty>
                          {
                            PaddockData.map(
                              (item, index) => (<MenuItem key={item.id} value={item.name} onClick={(event) => handleChange(event, item.name)}>{item.name}</MenuItem>)
                            )
                        }
                    </MuiSelect>
               </FormControl>
                </Box>
              </DialogContent>
              <DialogActions>
                  <Button onClick={handleClose} color="primary">CANCEL</Button>
                  <Button onClick={handleOk} color="primary">SAVE</Button>
              </DialogActions>
          </Dialog> 
          <Dialog open={openedit} onClose={handleClose}>
          <DialogTitle>Farms/Paddocks</DialogTitle>
                <DialogContent>
                  <Box component="form" sx={{ display: 'grid' }}>
                  <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                    <InputLabel>Farm</InputLabel>
                      <MuiSelect
                          label="Farm"
                          name="farms"
                          value={EditValues.farms}
                          onChange={handleChange1}
                          displayEmpty>
                          {
                            farmData.map(
                              (item, index) => (<MenuItem key={item.id} value={item.value} >{item.value}</MenuItem>)
                            )
                        }
                      </MuiSelect>
                  </FormControl>
                  <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                    <InputLabel>Paddock</InputLabel>
                    <MuiSelect
                        label="Paddock"
                        name="paddocks"
                        value={EditValues.paddocks}
                        onChange={handleChange1}
                          displayEmpty>
                          {
                            PaddockData.map(
                              (item, index) => (<MenuItem key={item.id} value={item.name} >{item.name}</MenuItem>)
                            )
                        }
                    </MuiSelect>
               </FormControl>
                </Box>
              </DialogContent>
              <DialogActions>
                 <Button onClick={handleRemove} color="secondary">REMOVE</Button>
                  <Button onClick={handleClose} color="primary">CANCEL</Button>
                  <Button onClick={handleOk} color="primary">SAVE</Button>
            </DialogActions>
        </Dialog>
        </div>
    );
}
