import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import FormControl from '@mui/material/FormControl';
import { InputLabel, MenuItem, Grid, Select as MuiSelect } from '@material-ui/core';
import question from './question mark.png'

export default function Farms(props) {
    const [open, setOpen] = React.useState(false);
    const [category, setcategory] = React.useState("");
    const [cat, setcat] = React.useState({});
    const [img, setimg] = React.useState(question);
    const [img1, setimg1] = React.useState(question);
    var changeCategory = 0;
    var flag=0;
    const arr = {};
    const [field, setfield] = React.useState("CATEGORY")
    const [fieldfarm, setfieldfarm] = React.useState("")
    const [subfield, setSubfield] = React.useState('NONE');
    const [fieldval, setfieldval] = React.useState("CATEGORY")
    const [subfieldval, setSubfieldval] = React.useState('NONE');
    const [c, setc] = React.useState({});

    const options= () => ([
        {id:'1', title:'Equipment',data:{'Equipment':['Equ1']},icon:tractor},
        {id:'2', title:'Paddock',data:{'Farm':['Farm1','Farm2'],'Paddock':['Pad1']},icon:paddock},
        {id:'3', title:'Fuel Tank',data:{'Farm':['Farm1'],'Fuel Tank':['Fuel1']},icon:fueltank},
        {id:'4', title:'Silo',data:{'Farm':['Farm1'],'Silo':['Silo1']},icon:silo},
        {id:'5', title:'Livestock',data:{'Livestock':['Livestock1']},icon:livestock}
    ])
    

    const handleChange = (event, value) => {
        setcategory(event.target.value);
        changeCategory = event.target.value;
        setfieldval(value.props.children)
        setcat({})
        flag=0;
        const arr = options.filter(
            a => (a.id === changeCategory)
        )
        if (changeCategory != "") {
            setc(arr[0].data);
            setSubfieldval("");
            setfieldfarm("");
            setimg1(options[changeCategory - 1].icon);
        }
        else{
            setc({});
            setfieldval("CATEGORY")
            setSubfieldval("None");
            setfieldfarm("");
            setimg1(question);
        }
    };

    const handleChange1 = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setcat(values => ({ ...values, [name]: value }))
        console.log(cat);
        if (name == "Farm") {
            setfieldfarm(value + " : ");
            flag=1;
        }
        else {
            setSubfieldval(value);
        }
    }
    const handleClickOpen = () => {
        if (!restart) {
            setOpen(true);
        }
    };

    const handleClose = (event, reason) => {
        if (reason !== 'backdropClick') {
            setOpen(false);
        }
    };

    const handleOk = (event, reason) => {
        if (reason !== 'backdropClick') {
            if (subfieldval=="") {
                alert("No "+fieldval+" Selected")
            }
            else{
                if(fieldfarm!=""){
                    setfield(fieldval);
                    setSubfield(fieldfarm + subfieldval);
                    setimg(img1)
                    setOpen(false);
                }
                else{
                    if(category==1 ||category==5){
                        setfield(fieldval);
                        setSubfield(fieldfarm + subfieldval);
                        setimg(img1)
                        setOpen(false);
                    }
                }
            }
        }
    };


    const { name, label, value, onChange, restart } = props;
    return (
        <div>
            
                               <Box component="form" sx={{ display: 'grid' }}>
                        <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                            <InputLabel>{label}</InputLabel>
                            <MuiSelect
                                label={label}
                                name={name}
                                value={category}
                                onChange={handleChange}
                                displayEmpty>
                                <MenuItem value="">
                                    <em>None</em>
                                </MenuItem>
                                {
                                    options.map(
                                        item => (<MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>)
                                    )
                                }
                            </MuiSelect>
                        </FormControl>
                        {
                            Object.entries(c).map(
                                ([key, val]) => (
                                    <FormControl variant="standard" sx={{ m: 1, minWidth: 420 }}>
                                        <InputLabel>{key}</InputLabel>
                                        <MuiSelect
                                            name={key}
                                            label={key}
                                            value={cat[key]}
                                            onChange={handleChange1}
                                        >
                                            {
                                                val.map(
                                                    (item, index) => (<MenuItem key={index} value={item}>{item}</MenuItem>)
                                                )
                                            }
                                        </MuiSelect>
                                    </FormControl>)
                            )
                        }
                    </Box>
               
        </div>
    );
}
