import React from "react";
import MaterialTable, { Column } from "material-table";
import "../../Pages.css";
import CreateIcon from "@mui/icons-material/Create";
import AddCircleRoundedIcon from "@mui/icons-material/AddCircleRounded";
import DeleteIcon from "@mui/icons-material/Delete";
import Chip from "@mui/material/Chip";
import UserData from "../../../../Data/UserData";
import { Modal, Button, Dropdown, Form } from "semantic-ui-react";

const User_types = ["Owner", "Admin", "User"];
const options = [
  { key: "payroll", text: "Payroll", value: "payroll" },
  { key: "add/editusers", text: "Add/Edit Users", value: "add/editusers" },
  { key: "graininventory", text: "Grain Inventory", value: "graininventory" },
  { key: "graincontracts", text: "Grain Contracts", value: "graincontracts" },
  {
    key: "chemicalinventory",
    text: "Chemical Inventory",
    value: "chemicalinventory",
  },
  {
    key: "fertiliserinventory",
    text: "Fertiliser Inventory",
    value: "fertiliserinventory",
  },
  { key: "fuelinventory", text: "Fuel Inventory", value: "fuelinventory" },
  { key: "waterinventory", text: "Water Inventory", value: "waterinventory" },
  { key: "spraylogs", text: "Spray Logs", value: "spraylogs" },
  {
    key: "spraylogapproval",
    text: "Spray Log Approval",
    value: "spraylogapproval",
  },
  { key: "plantings", text: "Plantings", value: "plantings" },
  { key: "spreadings", text: "Spreadings", value: "spreadings" },
  { key: "harvests", text: "Harvests", value: "harvests" },
  {
    key: "paddockmiantainance",
    text: "Paddock Miantainances",
    value: "paddockmiantainance",
  },
  { key: "irigation", text: "Irigation", value: "irigation" },
  { key: "grazing", text: "Grazing", value: "grazing" },
];
const roles = [
  { key: "user", text: "User", value: "user" },
  { key: "admin", text: "Admin", value: "admin" },
];
const User_permissions = [
  "Payroll",
  "Add/Edit Users",
  "Grain Inventory",
  "Grain Contracts",
  "Chemical Inventory",
  "Fertiliser Inventory",
  "Fuel Inventory",
  "Water Inventory",
  "Spray Logs",
  "Spray Log Approval",
  "Plantings",
  "Spreadings",
  "Harvests",
  "Paddock Miantainance",
  "Irigation",
  "Grazing",
];
const users_data = [
  {
    Name: "Alen Siby",
    user_role: "Owner",
    permissions: "All",
    Phone: "7034726499",
    email: "alensiby6@gmail.com",
    Address: "asdasdnasd,asfjksdlfm,asjfksdnf,684522,kerala,India",
  },
  {
    Name: "Align Elsa",
    user_role: "User",
    permissions: "Payroll, Add/Edit Users, Grain Inventory",
    Phone: "7034726499",
    email: "alensiby6@gmail.com",
    Address: "asdasdnasd,asfjksdlfm,asjfksdnf,684522,kerala,India",
  },
  {
    Name: "Ivin Siby",
    user_role: "Admin",
    permissions: "Payroll, Add/Edit Users, Grain Inventory",
    Phone: "7034726499",
    email: "alensiby6@gmail.com",
    Address: "asdasdnasd,asfjksdlfm,asjfksdnf,684522,kerala,India",
  },
];
var chipcolor = "";

export default function Users() {
  const [open, setOpen] = React.useState(false);
  const [confopen, setconfOpen] = React.useState(false);

  const columns = [
    {
      title: "Name",
      field: "Name",
    },
    {
      title: "User Role",
      field: "user_role",
      render(rowdata) {
        function Switches(rowdata) {
          if (rowdata.user_role === "Owner") {
            chipcolor = "error";
          } else if (rowdata.user_role === "User") {
            chipcolor = "primary";
          } else {
            chipcolor = "warning";
          }

          return (
            <div>
              <Chip color={chipcolor} label={rowdata.user_role} />
            </div>
          );
        }
        return Switches(rowdata);
      },
    },
    {
      title: "Permissions",
      field: "permissions",
    },
    {
      title: "Phone",
      field: "Phone",
    },
    {
      title: "Email",
      field: "email",
    },
    {
      title: "Address",
      field: "Address",
    },
  ];
  return (
    <div className="table-size">
      <div className="subheader">
        <h1 style={{ color: "black", "margin-bottom": "0px" }}>Users </h1>
        <span>&nbsp;&nbsp;&nbsp;&nbsp;</span>
      </div>
      <Modal onClose={() => setOpen(false)} open={open} as={Form}>
        <Modal.Header>New User</Modal.Header>
        <Modal.Content>

          <Form>
            <Form.Group widths="equal">
              <Form.Input
                fluid
                required
                label="First name "
                placeholder="First name"
                id="form-input-first-name"
              />
              <Form.Input
                required={["required"]}
                fluid
                validators={["required"]}
                label="Last name "
                placeholder="First name"
                id="form-input-last-name"
              />
            </Form.Group>
            <Form.Group inline>
              <Form.Field required>
                <label>User Roles</label>
                <Dropdown
                  widths={4}
                  fluid
                  clearable
                  selection
                  options={roles}
                />
              </Form.Field>
              <Form.Field>
                <label>User Permissions</label>
                <Dropdown
                  label="User Roles"
