import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import { Useform, Form } from '../Useform';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import FormControl from '@mui/material/FormControl';
import SelectCheck from './SelectCheck';
import { InputLabel, MenuItem, Grid, Select as MuiSelect } from '@material-ui/core';
import TextField from '@mui/material/TextField';
import tractor from '../services/tractor.png'

export default function Equipment(props) {
    const [open, setOpen] = React.useState(false);
    const [equipment, setequipment] = React.useState("");
    const [droplet, setDroplet] = React.useState("");
    const [img, setimg] = React.useState(tractor);
    const [img1, setimg1] = React.useState(tractor);
    var changeequipment = 0;
    var flag=0;
    const arr = {};
    const [field, setfield] = React.useState("None")
    const [fieldfarm, setfieldfarm] = React.useState("")
    const [subfield, setSubfield] = React.useState('NONE');
    const [rate, setRate] = React.useState('60');
    const [rateunit, setRateunit] = React.useState('l/Ha');
    const [fieldval, setfieldval] = React.useState("equipment")
    const [subfieldval, setSubfieldval] = React.useState('NONE');
    const [c, setc] = React.useState({});

    const options= () => ([
        {id:'1', title:'Equipment',data:{'Equipment':['Equ1']}},
        {id:'2', title:'Paddock',data:{'Farm':['Farm1','Farm2'],'Paddock':['Pad1']}},
        {id:'3', title:'Fuel Tank',data:{'Farm':['Farm1'],'Fuel Tank':['Fuel1']}},
        {id:'4', title:'Silo',data:{'Farm':['Farm1'],'Silo':['Silo1']}},
        {id:'5', title:'Livestock',data:{'Livestock':['Livestock1']}}
    ])
    const initialValues = {
  
        nozzles: '',
        equipment: '',
        dueDate: new Date(),
        priority: '',
        started: '',
        completed: '',
        restartjob: '',
        description: "",
        notes: "",
    }
    
    const {
        values,
        setValues,
        handleInputChange
    } = Useform(initialValues);


    const handleChange = (event, value) => {
        setequipment(event.target.value);
        changeequipment = event.target.value;
        setfieldval(value.props.children)
       
        flag=0;
        
        if (changeequipment != "") {
           
            setSubfieldval("");
            setfieldfarm("");
          
        }
        else{
          
            setfieldval("equipment")
            setSubfieldval("None");
            setfieldfarm("");
        
        }
    };

  
    const handleClickOpen = () => {
      
            setOpen(true);
     
    };

    const handleClose = (event, reason) => {
     
            setOpen(false);
      
    };
   
    const { name, label, value, onChange,restart } = props;
    return (
        <div>
            <Button color="inherit" onClick={handleClickOpen} size="large" fullWidth="true">
                <Grid container spacing={0}>
                    <Grid item xs={0} sm={0} md={0}>
                        <img src={img} className="img" alt="tractor"></img>
                    </Grid>
                    <Grid item xs={6} md={3}>
                        <p>{field}</p>
                        <InputLabel>Nozzles:{subfield}</InputLabel>
                        <p >{rate} {rateunit}</p>

                    </Grid>
                </Grid>
            </Button>
           
            
        </div>
    );
}
