import React, { Component } from "react";
import { useState } from "react";
import {Link, useHistory } from "react-router-dom";
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './login.css';
function Login() {
    const [email,setEmail]=useState('');
    const [password,setPassword]=useState('');
    const mymail="alignagith@gmail.com";
    const mypass="123";
  let history = useHistory();
    
const handleSubmit=()=>{
  if(email===mymail && password===mypass) {
    history.push("../Job");
  }
    else{
        alert("Login failed");
    }
}




        return (
            <div className="body bgp">
     
      <div className="outer">
        <div className="inner">
            <form>

                <h3>Login</h3>

                <div className="form-group">
                    <label>Email</label>
                    <input name="email" type="email" className="form-control" placeholder="Enter email" onChange={e => setEmail(e.target.value)} />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input  type="password" name="password" className="form-control" placeholder="Enter password" onChange={e => setPassword(e.target.value)} />
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>
             <div className="form-group">
               <button type="submit" onClick={handleSubmit} className="btn btn-dark btn-lg btn-block mybtn"style={{ "background-color": "green"}} >Sign in</button>
               
            
                </div>
             <div className="form-group">
                 
                <p> <span className="forgot-password">
                    New User? <a href="#">Register</a></span>
                <span className="forgot-password text-right" style={{"float": "right"}}>
                     <a href="#">Forgot password?</a>
                </span></p>
            
                </div>
            
            </form>
        </div>
      </div>
     </div>
        );
    }
        
export default Login
