import React, {useState} from 'react'
import { Button, Icon, Modal, Input } from 'semantic-ui-react'
import GearIcon from 'mdi-react/GearIcon';
import farmdata from "../../../../../Data/farmData";

export default function Farmpopup(props) {
  const Value = props.Value;
  const [firstOpen, setFirstOpen] = React.useState(false)
  const [addOpen, setAddOpen] = React.useState(false)
  const [editOpen, setEditOpen] = React.useState(false)
  const [deleteOpen, setDeleteOpen] = React.useState(false)

  const [state, setState] = useState(); 
  const data = farmdata.find((d) => d.id === Value);

        if (data) {
            setState(data.value);
            alert("farm")
        }
  const [farm,setfarm]=useState(farmdata);
const addRow=()=>{
  let newFarm={value:{state}}
  setfarm([...farm,newFarm])
  alert("New farm added");
}
const editRow=()=>{
 
  alert("Farm edited");
}
const deleteRow=()=>{
 
  alert("Farm deleted");
}
  return (
    <>
     
      <GearIcon onClick={() => setFirstOpen(true)} style={{"cursor":"pointer", "color": "black"}}/>
      <Modal style={{ "height": "35%", "width": "25%", "background-color": "rgb(0 0 0 / 62%)" }}
        onClose={() => setFirstOpen(false)}
        onOpen={() => setFirstOpen(true)}
        open={firstOpen}
      >
        <Modal.Header>Farm Options</Modal.Header>
        <Modal.Content>
        <div  style={{"margin-left":"65px"}}>
          <Modal.Description>
          
          <Button basic color='green' onClick={() => setAddOpen(true)}>
          <span style={{"fontSize":"18px", "color":"white"}}>.</span>
          <Icon name='plus' />
          <span style={{"fontSize":"18px", "color":"white"}}>.</span>
            Add Farm 
            <span style={{"fontSize":"18px", "color":"white"}}>..</span>
          </Button>
          
          </Modal.Description>
         
         
          <Modal.Description style={{"margin-top":"10px"}}>
          <Button basic color='blue' onClick={() => setEditOpen(true)}>
          
          <span style={{"fontSize":"18px", "color":"white"}}>.</span>
          <Icon name='pencil' />
          <span style={{"fontSize":"18px", "color":"white"}}>.</span>
            Edit Farm 
            <span style={{"fontSize":"18px", "color":"white"}}>..</span>
          </Button>
          
          </Modal.Description>
         
         
          <Modal.Description style={{"margin-top":"10px"}}>
          <Button basic color='red' onClick={() => setDeleteOpen(true)
          }>
          <Icon name='minus' />
            Delete Farm
          </Button>
          
          </Modal.Description>
         </div>
        </Modal.Content>
        
        <Modal.Actions>
        <Button color='red' onClick={() => setFirstOpen(false)}>
          <Icon name='remove'  /> Cancel
        </Button>
          
        </Modal.Actions>
            
      <Modal
          onClose={() => setAddOpen(false)}
          open={addOpen}
          size='small'
          style={{ "height": "35%", "width": "35%" }}
        >
          <Modal.Header>New Item</Modal.Header>
          <Modal.Content>
          <Input fluid iconPosition='left' placeholder='Name'>
              
          <input onChange={(e) => setState(e.target.value)
          } />
    </Input>
   
          </Modal.Content>
          <Modal.Actions>
          <Button
              icon='close'
              content='Cancel'
              onClick={() => setAddOpen(false)}
            />
            <Button positive
              icon='check'
              content='Save'
              onClick={() => {setAddOpen(false);
              addRow();}}
            />
          </Modal.Actions>
        </Modal>
        
      <Modal
          onClose={() => setEditOpen(false)}
          open={editOpen}
          size='small'
          style={{ "height": "35%", "width": "35%" }}
        >
          <Modal.Header>Edit</Modal.Header>
          <Modal.Content>
          <Input fluid iconPosition='left'>
              
          <input value={state} onChange={(e) => setState(e.target.value)} />
    </Input>
    
          </Modal.Content>
          <Modal.Actions>
          <Button
              icon='close'
              content='Cancel'
              onClick={() => setEditOpen(false)}
            />
            <Button positive
              icon='check'
              content='Save'
              onClick={() => {setEditOpen(false); editRow();}}
            />
          </Modal.Actions>
        </Modal>
        
      <Modal
          onClose={() => setDeleteOpen(false)}
          open={deleteOpen}
          size='small'
          style={{ "height": "35%", "width": "25%" }}
        >
          <Modal.Header>Delete</Modal.Header>
          <Modal.Content>
            <p>Are you sure you want to delete {state}?</p>
          </Modal.Content>
          <Modal.Actions>
            <Button  onClick={() => setDeleteOpen(false)} color="white"
             icon='close' 
             content='Cancel'
             />
            <Button onClick={() => {setDeleteOpen(false);
            deleteRow();}} 
            negative
           
             content='Delete'
             />
          </Modal.Actions>
          
          
        </Modal>
        
      </Modal>
    </>
  )
}
